unitsize(1cm);
draw(Circle((0,0),2),linewidth(0.7));
draw((1.7,1)--(-1.7,-1),linewidth(0.7));
draw((1.7,-1)--(-1.7,1),linewidth(0.7));
draw((0,2)--(0,-2));
label("1",(0.8,0.5),NW);
label("2",(0.8,-0.5),SW);
label("6",(-0.8,0.5),NE);
label("9",(-0.8,-0.5),SE);
label("3",(-0.7,0),W);
label("7",(0.7,0),E);
draw((-2.8,0)--(-2.1,0),Arrow);
