draw((3,-1)--(6,-1),linewidth(0.7));
draw((3,0)--(6,0),linewidth(0.7));
draw((3,1)--(6,1),linewidth(0.7));
draw((4,-2)--(4,2),linewidth(0.7));
draw((5,-2)--(5,2),linewidth(0.7));
label("1",(3.5,-2),S);
label("2",(4.5,-2),S);
label("3",(5.5,-2),S);
label("1",(3,-1.5),W);
label("2",(3,-0.5),W);
label("3",(3,0.5),W);
label("4",(3,1.5),W);
[/asy]

