جوان اپنے مجموعے کے ڈاک ٹکٹوں کو ملک اور دہائی کے حساب سے ترتیب دیتا ہے جس میں وہ جاری کیے گئے تھے۔ اس نے ان کے لیے جو قیمتیں ایک ڈاک ٹکٹ کی دکان پر ادا کیں وہ یہ تھیں: برازیل اور فرانس، ہر ایک $6$ سینٹ، پیرو $4$ سینٹ، اور اسپین $5$ سینٹ۔ (برازیل اور پیرو جنوبی امریکہ کے ممالک ہیں اور فرانس اور اسپین یورپ میں ہیں۔) [asy]
/* AMC8 2002 #8, 9, 10 مسئلہ */
size(3inch, 1.5inch);
for ( int y = 0; y <= 5; ++y )
{

draw((0,y)--(18,y));
}
draw((0,0)--(0,5));
draw((6,0)--(6,5));
draw((9,0)--(9,5));
draw((12,0)--(12,5));
draw((15,0)--(15,5));
draw((18,0)--(18,5));

label(scale(0.8)*"50s", (7.5,4.5));
label(scale(0.8)*"4", (7.5,3.5));
label(scale(0.8)*"8", (7.5,2.5));
label(scale(0.8)*"6", (7.5,1.5));
label(scale(0.8)*"3", (7.5,0.5));

label(scale(0.8)*"60s", (10.5,4.5));
label(scale(0.8)*"7", (10.5,3.5));
label(scale(0.8)*"4", (10.5,2.5));
label(scale(0.8)*"4", (10.5,1.5));
label(scale(0.8)*"9", (10.5,0.5));

label(scale(0.8)*"70s", (13.5,4.5));
label(scale(0.8)*"12", (13.5,3.5));
label(scale(0.8)*"12", (13.5,2.5));
label(scale(0.8)*"6", (13.5,1.5));
label(scale(0.8)*"13", (13.5,0.5));

label(scale(0.8)*"80s", (16.5,4.5));
label(scale(0.8)*"8", (16.5,3.5));
label(scale(0.8)*"15", (16.5,2.5));
label(scale(0.8)*"10", (16.5,1.5));
label(scale(0.8)*"9", (16.5,0.5));

label(scale(0.8)*"ملک", (3,4.5));
label(scale(0.8)*"برازیل", (3,3.5));
label(scale(0.8)*"فرانس", (3,2.5));
label(scale(0.8)*"پیرو", (3,1.5));
label(scale(0.8)*"اسپین", (3,0.5));

label(scale(0.9)*"جوان کا ڈاک ٹکٹ مجموعہ", (9,0), S);
label(scale(0.9)*"دہائی کے حساب سے ڈاک ٹکٹوں کی تعداد", (9,5), N);
[/asy] اس کے $70\text{'s}$ کے ڈاک ٹکٹوں کی اوسط قیمت، سینٹ میں، کیا تھی؟ اپنے جواب کو قریب ترین دسویں سینٹ تک گول کریں۔

original_question:
مثلث $ABC$ ایک متساوی الساقین مثلث ہے، جس میں $AB=AC$ اور بلندی $AM=11$ ہے۔ فرض کریں کہ $\overline{AM}$ پر ایک نقطہ $D$ ہے جس کے لیے $AD=10$ اور $\angle BDC=3\angle BAC$ ہے۔ مثلث $ABC$ کا محیط معلوم کریں۔

[asy]
unitsize(0.4 cm);

pair A, B, C, D, M;

A = (0,11);
D = (0,1);
B = (-11/2,0);
C = (11/2,0);
M = (B + C)/2;

draw(A--B--C--cycle);
draw(A--M);
draw(B--D--C);

label("$A$", A, N);
label("$B$", B, SW);
label("$C$", C, SE);
label("$D$", D, NW);
label("$M$", M, S);
[/asy]

