draw(C--P,red);
draw(D--P,red);
draw(E--P,red);
draw(F--P,red);

draw(A--C);
draw(A--D);
draw(A--E);
draw(A--F);
draw(C--E--D--F--cycle);
draw(D--B--C,dashed);
draw(B--C,dashed);
draw(B--D,dashed);
draw(B--E,dashed);
draw(B--F,dashed);

label("$P$", P, NE);
[/asy]

