def find_common_factors(n1, n2):
    # Find the prime factorizations of n1 and n2
    prime_factors_n1 = prime_factorization(n1)
    prime_factors_n2 = prime_factorization(n2)
    
    # Determine the GCD of n1 and n2
    gcd = get_gcd(prime_factors_n1, prime_factors_n2)
    
    # Find all the positive two-digit factors of the GCD
    two_digit_factors = [factor for factor in divisors(gcd) if 10 <= factor < 100]
    
    return len(two_digit_factors)

